import React, { useState, createContext } from "react";
import Languages from "./Languages";
import NavBarLinks from "./NavBarLinks";
import Temp from "./Temp";
import MainContainer from "./MainContainer"
import SecondContainer from "./SecondContainer"
import Date from "./Date"
import NavBarLinksTwo from "./NavBarLinksTwo";
import EmptySpace from "./EmptySpace";
import BigArticle from './BigArticle'
import LinearDeterminate from './LinearDeterminate'
import useEvent from './useEventHook'
import {
   BrowserRouter as Router,
   Switch,
   Route,
   Link
} from "react-router-dom";
import useFetch from "./myFetchHook";


export const DataProvider = createContext()

export default function NavegationBar() {

   const [navData, setNavData] = useState();
   const [sourceData, setSourceData] = useState();
   const [langData, setLangData] = useState();
   
   let firstEventToSend = (e , url) => {
      let valueToSend = {
         topic: e.target.innerText
      }
      const options = {
         method: 'POST',
         headers: {
            'Content-Type': 'application/json'
         },
         body: JSON.stringify(valueToSend)
      };
      fetch(`${url}`, options)
         .then((response) => response.json())
         .then((data) => {
            setNavData(data.articles);
            console.log(data.articles)
         })
   }
   

   let secondEventToSend = (e) => {
      let valueToSend = {
         topic: e.target.innerText
      }
      const options = {
         method: 'POST',
         headers: {
            'Content-Type': 'application/json'
         },
         body: JSON.stringify(valueToSend)
      };
      fetch('/sources', options)
         .then((response) => response.json())
         .then((data) => {
            setSourceData(data.articles);
            console.log(data.articles)
         })
   }
   let thirdEventToSend = (e) => {
      let valueToSend = {
         topic: e.target.innerText
      }
      console.log(e.target.innerText)

      const options = {
         method: 'POST',
         headers: {
            'Content-Type': 'application/json'
         },
         body: JSON.stringify(valueToSend)
      };
      fetch('/lang', options)
         .then((response) => response.json())
         .then((data) => {
            setLangData(data.articles);
            console.log(data)
         })
   }



   return (


      <Router>

         <div>

            <div className="navbar" >
               <ul className="navUl">
                  {/* <Temp /> */}
                  <h1 className="logo">

                     <Link to="/"><p>~D|Fetch~</p></Link>
                  </h1>

                  <NavBarLinks onClick={firstEventToSend}
                     className="linksNav"
                     name="Topics"
                     link1="Health"
                     link2="Science"
                     link3="Sports"
                     link4="Entertainment"
                     link5="Business"
                     link6="Technology"
                  />
                  <NavBarLinksTwo onClick={secondEventToSend}
                     className="linksNav"
                     name="Sources"
                     link1="The-Wall-Street-Journal"
                     link2="National-Geographic"
                     link3="ESPN"
                     link4="Time"
                     link5="Reuters"
                     link6="BBC-NEWS"
                     link7="CNN"

                  />
                  <Languages onClick={thirdEventToSend}
                     className="linksNav"
                     name="Lan"
                     link1="English"
                     link2="Spanish"
                     link3="Russian"
                  />
               </ul>

               <Date />

            </div>
            <EmptySpace />

            <Switch>

               <Route exact path="/topics">
                  {navData ? <div className="navbar-container">
                     <div className="left-section-news">
                        {navData
                           .filter(
                              (art) => !!art.description && !!art.urlToImage && !!art.author
                           )
                           .slice(0, 10)
                           .map((article) => (
                              <BigArticle
                                 key={article.url}
                                 headline={article.title}
                                 author={article.author}
                                 time={article.publishedAt}
                                 description={article.description}
                                 imgURL={article.urlToImage}
                                 url={article.url}
                              />
                           ))}
                     </div>
                  </div> : <LinearDeterminate />}
               </Route>
               <Route exact path="/sources">
                  {sourceData ? <div className="navbar-container">
                     <div className="left-section-news">
                        {sourceData
                           .filter(
                              (art) => !!art.description && !!art.urlToImage && !!art.author
                           )
                           .slice(0, 10)
                           .map((article) => (
                              <BigArticle
                                 key={article.url}
                                 headline={article.title}
                                 author={article.author}
                                 time={article.publishedAt}
                                 description={article.description}
                                 imgURL={article.urlToImage}
                                 url={article.url}
                              />
                           ))}
                     </div>
                  </div> : <LinearDeterminate />}
               </Route>
               <Route exact path="/lang">
                  {langData ? <div className="navbar-container">
                     <div className="left-section-news">
                        {langData
                           .filter(
                              (art) => !!art.description && !!art.urlToImage && !!art.author
                           )
                           .slice(0, 10)
                           .map((article) => (
                              <BigArticle
                                 key={article.url}
                                 headline={article.title}
                                 author={article.author}
                                 time={article.publishedAt}
                                 description={article.description}
                                 imgURL={article.urlToImage}
                                 url={article.url}
                              />
                           ))}
                     </div>
                  </div> : <LinearDeterminate />}
               </Route>
               <Route exact path="/">

                  <MainContainer />
                  <SecondContainer />
               </Route>
            </Switch>
         </div>
      </Router>

   );
}
