import React, {useState , useEffect} from 'react';
import HeadlineWithImg from './HeadlineWithImg';
import XlArticle from './XlArticle';



function MiddleSection() {

    const [items, setItems] = useState([]);
  
    useEffect( () => {
          loadData();
    }, [items]);
  
    const loadData = async () => {
         await fetch('/source')
        .then(res => res.json())
        .then(data => setItems(data.data.articles));
      }
      
    

return( 
    <div className="middle-section-news">
        {items.slice(5,6).map(article => (<XlArticle imgURL={article.urlToImage} headline={article.title} url={article.url} />))}
        {items.slice(6,7).map(article => (<HeadlineWithImg imgurl={article.urlToImage} headline={article.title} url={article.url} />))}
        
    </div>
);
}


export default MiddleSection;
