import React, {useState , useEffect} from 'react';
import SmallHeadlines from './SmallHeadlines';
import HeadlineWithImg from './HeadlineWithImg';



function MiddleSection() {

    const [items, setItems] = useState([]);
  
    useEffect( () => {
          loadData();
    }, [items]);
  
    const loadData = async () => {
         await fetch('/source')
        .then(res => res.json())
        .then(data => setItems(data.data.articles));
      }
      
    

return( 
    <div className="middle-section-news">
        {items.slice(4,12).map(article => (<><HeadlineWithImg key={article.url} 
                                                    headline ={article.title} 
                                                    imgURL={article.urlToImage} 
                                                    url={article.url}/>
                                                    <SmallHeadlines headline={article.title} url={article.url}/>
                                                    <SmallHeadlines headline={article.title} url={article.url}/>
                                                    <SmallHeadlines headline={article.title} url={article.url}/>
                                                    <SmallHeadlines headline={article.title} url={article.url}/>
                                                    <SmallHeadlines headline={article.title} url={article.url}/>
                                            </>
                                            ))}
       
    </div>
);
}


export default MiddleSection;
