import { useState, useEffect } from "react";

const useFetch = (url) => {
   const [data, setData] = useState();

   useEffect(() => {
      const fetchData = async () => {
         fetch(url)
            .then((res) => res.json())
            .then((result) => {
               setData(result.articles);
               console.log(result);
               S;
            });
      };
      fetchData();
   }, [url]);

   return { data };
};
export default useFetch;
