import React, { useState, useEffect } from "react";
import ComPage from "../subRoutes/comPage";

function SearchPage({ liftedState }) {
   const [text, settext] = useState();
   const [state, setstate] = useState();

   useEffect(() => {
      settext(liftedState.input);
   }, []);

   let myHeaders = new Headers();
   myHeaders.append("Content-Type", "application/x-www-form-urlencoded");

   let urlencoded = new URLSearchParams();
   urlencoded.append("question", "miami dade");
   urlencoded.append("language", "en");

   let requestOptions = {
      method: "POST",
      headers: myHeaders,
      body: urlencoded,
      redirect: "follow",
   };
   useEffect(() => {
      fetch("/search", requestOptions)
         .then((response) => response.json())
         .then((result) => setstate(result.articles))
         .catch((error) => console.log("error", error));
   }, []);

   return (
      <div>
         <div className="emptySpace">
            searching for articles with "{text}" on the title search true or
            false? "{liftedState.toggle}"
         </div>
         {liftedState.toggle ? (
            <ComPage news={state} />
         ) : (
            <div className="emptySpace">please start seacrhing</div>
         )}
      </div>
   );
}

export default SearchPage;
