import Footer from "./components/Footer";
import React, { useState } from "react";
import update from "immutability-helper";
import EmptySpace from "./components/EmptySpace";
import SearchPage from "./components/SearchPage";
import SourcesPage from "./components/SourcesPage";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import TopicsPage from "./components/TopicsPage";
import NavigationBar from "./components/NavigationBar";
import "./App.css";
import HomePage from "./components/HomePage";
import { createMuiTheme, ThemeProvider } from "@material-ui/core";
import PhoneScreens from "./Hidden/PhoneScreens";
import Tablet from "./Hidden/Tablet";
import LangPage from "./components/LangPage";

export default function App() {
   const [liftedState, setliftedState] = useState({
      input: "",
      ready: "",
   });
   const [Search, setSearch] = useState();
   const [Language, setLanguage] = useState();

   const theme = createMuiTheme({
      palette: {
         text: {
            primary: "#4a4e69",
            secondary: "#9a8c98",
         },
      },
   });

   return (
      <ThemeProvider theme={theme}>
         <Router>
            <NavigationBar />
            <EmptySpace setliftedState={setliftedState} />

            <Switch>
               <Route exact path="/search">
                  <SearchPage liftedState={liftedState} />
               </Route>

               <Route path="/topics">
                  <TopicsPage />
               </Route>

               <Route path="/sources">
                  <SourcesPage />
               </Route>

               <Route path="/lang">
                  <LangPage />
               </Route>

               <Route exact path="/">
                  {/* <HomePage /> */}
                  {/* <PhoneScreens />
                  <Tablet /> */}
               </Route>
            </Switch>
            <Footer />
         </Router>
      </ThemeProvider>
   );
}
