import React , { useState , useEffect } from 'react';
import BigArticle from './BigArticle';

function LeftSideNews() {
    const [news, setNews] = useState({})

    useEffect(() => {
      fetch('/source')
            
            .then(res => {
                return res.json();
            })
            .then(res => {
                setNews({res});
                console.log(news.data.articles);
                // let newOne = news.data.articles[0].title
                // console.log(newOne) 
            })
            .catch(err => {
                console.log('there is an error')
            });
       
    }, [])

    
    return( <div className="left-section-news">
            {/* {news.data.articles.map((item)=>{ <BigArticle  headline={item.title} description="hello" imgURL=""/>})} */}
            
            </div>);
}



//headline="" description="" imgURL="" headline={news[0].title}

export default LeftSideNews;
