import React, { useEffect, useState } from 'react'

export default useFetch = (url) => {
    [items, setItems] = useState({ items: null })


    useEffect(() => {

        fetch(url)
            .then((res) => res.json())
            .then((data) => setItems(data.articles));
    })
    return useFetch()
}
