import React, { useState, useEffect } from "react";
import BigArticle from "./BigArticle";
import XlArticle from "./XlArticle";
import LinearDeterminate from './LinearDeterminate';
import useFetch from './myFetchHook'

function LeftSideNews() {
   const { data, isLoading, hasError, errorMessage, updateUrl } = useFetch("/trump")


   return (
      <div className="left-section-news">
         {data ? data
            .filter((art) => !!art.urlToImage && !!art.url)
            .slice(0, 1)
            .map((article) => (
               <XlArticle
                  imgURL={article.urlToImage}
                  headline={article.title}
                  link={article.url}
               />
            )) : <LinearDeterminate />}
         {data ? data
            .filter(
               (art) => !!art.description && !!art.urlToImage && !!art.author
            )
            .slice(1, 3)
            .map((article) => (
               <BigArticle
                  key={article.url}
                  headline={article.title}
                  author={article.author}
                  time={article.publishedAt}
                  description={article.description}
                  imgURL={article.urlToImage}
                  url={article.url}
               />
            )) : <LinearDeterminate />}
      </div>
   );
}

export default LeftSideNews;
