import React, { useEffect, useState } from "react";

export default function Temp() {
   const [location, setLocation] = useState([]);
   const [icon, setIcon] = useState([]);
   const [threeDays, setThreeDays] = useState([]);

   const roundNumbers = (number) => Math.round((number - 273.15) * 1.8 + 32);

   function unixConvert(s) {
      return new Date(s * 1e3).toISOString().slice(-13, -5);
   }

   useEffect(() => {
      getLocation();
   }, []);
   //ask for location
   function getLocation() {
      if (navigator.geolocation) {
         navigator.geolocation.getCurrentPosition(showPosition);
      } else {
         console.log("couldnt set the position");
      }
   }
   //POST lat and long
   //set geolocation
   function showPosition(position) {
      let lat = position.coords.latitude;
      let lon = position.coords.longitude;
      const locationData = { lat, lon };
      const options = {
         method: "POST",
         headers: {
            "Content-Type": "application/json",
         },
         body: JSON.stringify(locationData),
      };
      fetch("/weather", options)
         .then((response) => response.json())
         .then((data) => {
            let weatherTemp = ((data.current.temp - 273.15) * 1.8 + 32).toFixed(
               0
            );
            let iconURL = data.current.weather[0].icon;
            let daysMap = data.daily;
            setLocation(weatherTemp);
            setIcon(iconURL);
            setThreeDays(data.daily);
            console.log(data);
         });
   }

   return (
      <div>
         <div className="temperature">
            <img
               src={`http://openweathermap.org/img/wn/${icon}.png`}
               alt=""
               srcSet=""
            />
            {location} °F{" "}
         </div>
         <div className="blurBack"></div>
         <div className="popup">
            {threeDays.slice(0, 3).map((day) => (
               <div className="popup_inner">
                  <div className="topInfo">
                     <div>Miami</div>
                     <div className="theDate">{unixConvert(day.dt)}</div>

                     <img
                        src={`http://openweathermap.org/img/wn/${day.weather[0].icon}.png`}
                        alt=""
                        srcSet=""
                     />
                     <div>{day.weather[0].description}</div>
                     <div className="theTemp">
                        {roundNumbers(day.temp.day)} °F
                     </div>
                  </div>
                  <div className="bottomInfo">
                     <div className="humidity">
                        <div>
                           humidity:
                           {day.humidity}%
                        </div>
                        <div>
                           pressure:
                           {day.pressure} hPa
                        </div>
                        <div>
                           wind:
                           {day.wind_speed} m/s
                        </div>
                     </div>
                     <div className="timeofday">
                        <div>
                           morning:
                           {roundNumbers(day.temp.morn)} °F
                        </div>
                        <div>
                           evening:
                           {roundNumbers(day.temp.eve)} °F
                        </div>
                        <div>
                           night:
                           {roundNumbers(day.temp.night)} °F
                        </div>
                     </div>
                     <div className="sunrise">
                        <div>
                           <div>sunrise</div>
                           <div>{unixConvert(day.sunrise)} AM</div>
                        </div>
                        <div>
                           <div>sunset</div>
                           <div>{unixConvert(day.sunset)} PM</div>
                        </div>
                     </div>
                  </div>
               </div>
            ))}
         </div>
      </div>
   );
}
