import React from "react";
import BigArticle from "./BigArticle";

function TopicsPage(props) {
   const topicsData = props.navData;
   const reactCard = {
      title: "react",
      category: "verb",
      definition: `
        1. act in response to something; respond in a particular way.
        "he reacted angrily to the news of his dismissal"
    `,
   };

   return (
      <div className="navbar-container">
         <div className="left-section-news">
            {topicsData
               .filter(
                  (art) => !!art.description && !!art.urlToImage && !!art.author
               )
               .slice(0, 15)
               .map((article) => (
                  <BigArticle
                     key={topicsData.article.url}
                     headline={topicsData.article.title}
                     author={topicsData.article.author}
                     time={topicsData.article.publishedAt}
                     description={topicsData.article.description}
                     imgURL={topicsData.article.urlToImage}
                     url={topicsData.article.url}
                  />
               ))}
         </div>
      </div>
   );
}

export default TopicsPage;
