import React, { useState, createContext, useEffect } from "react";
import Languages from "./Languages";
import NavBarLinks from "./NavBarLinks";
import Temp from "./Temp";
import MainContainer from "./MainContainer";
import SecondContainer from "./SecondContainer";
import Date from "./Date";
import NavBarLinksTwo from "./NavBarLinksTwo";
import EmptySpace from "./EmptySpace";
import BigArticle from "./BigArticle";
import LinearDeterminate from "./LinearDeterminate";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import topicsPage from "./topicsPage";

export const DataProvider = createContext();

export default function NavegationBar() {
   const [navData, setNavData] = useState();

   let firstEventToSend = (e, url) => {
      let valueToSend = {
         topic: e.target.innerText,
      };
      let options = {
         method: "POST",
         headers: {
            "Content-Type": "application/json",
         },
         body: JSON.stringify(valueToSend),
      };

      fetch(`${url}`, options)
         .then((response) => response.json())
         .then((data) => {
            setNavData(data.articles);
            localStorage.setItem("notes", JSON.stringify(data.articles));
            console.log(localStorage.getItem("notes"));
         });
   };

   return (
      <Router>
         <div>
            <div className="navbar">
               <ul className="navUl">
                  <Temp />
                  <h1 className="logo">
                     <Link to="/">
                        <p>~D|Fetch~</p>
                     </Link>
                  </h1>
                  <NavBarLinks
                     onClick={(e) => {
                        firstEventToSend(e, "/topics");
                     }}
                     className="linksNav"
                  />
                  <NavBarLinksTwo
                     onClick={(e) => {
                        firstEventToSend(e, "/sources");
                     }}
                     className="linksNav"
                  />
                  <Languages
                     onClick={(e) => {
                        firstEventToSend(e, "/lang");
                     }}
                     className="linksNav"
                  />
               </ul>
               <Date />
            </div>

            <EmptySpace />

            <Switch>
               <Route exact path="/topics">
                  {navData ? (
                     <div className="navbar-container">
                        <div className="left-section-news">
                           {navData
                              .filter(
                                 (art) =>
                                    !!art.description &&
                                    !!art.urlToImage &&
                                    !!art.author
                              )
                              .slice(0, 15)
                              .map((article) => (
                                 <BigArticle
                                    key={article.url}
                                    headline={article.title}
                                    author={article.author}
                                    time={article.publishedAt}
                                    description={article.description}
                                    imgURL={article.urlToImage}
                                    url={article.url}
                                 />
                              ))}
                        </div>
                     </div>
                  ) : (
                     <LinearDeterminate />
                  )}
               </Route>
               <Route exact path="/sources">
                  <topicsPage />
                  ) : (
                  <LinearDeterminate />
                  )}
               </Route>
               <Route exact path="/lang">
                  {navData ? (
                     <div className="navbar-container">
                        <div className="left-section-news">
                           {navData
                              .filter(
                                 (art) =>
                                    !!art.description &&
                                    !!art.urlToImage &&
                                    !!art.author
                              )
                              .slice(0, 15)
                              .map((article) => (
                                 <BigArticle
                                    key={article.url}
                                    headline={article.title}
                                    author={article.author}
                                    time={article.publishedAt}
                                    description={article.description}
                                    imgURL={article.urlToImage}
                                    url={article.url}
                                 />
                              ))}
                        </div>
                     </div>
                  ) : (
                     <LinearDeterminate />
                  )}
               </Route>
               <Route exact path="/">
                  {/* <MainContainer />
                  <SecondContainer /> */}
               </Route>
            </Switch>
         </div>
      </Router>
   );
}
