import React from 'react'
import { DataProvider } from './NavBar'
import { useContext, useState } from 'react'
import BigArticle from './BigArticle'


export default function TopicsPage() {

    const [navData, setNavData] = useContext(DataProvider);
    console.log(navData)
    fetch('/topics')
        .then((response) => response.json())
        .then((data) => {
            console.log(data);
        })
    return (
        <div className="secondContainer">hello
            <div className="left-section-news">
                {(() => {
                    if (navData = !undefined) {
                        navData
                            .filter(
                                (art) => !!art.description && !!art.urlToImage && !!art.author
                            )
                            .slice(6, 10)
                            .map((article) => (
                                <BigArticle
                                    key={article.url}
                                    headline={article.title}
                                    author={article.author}
                                    time={article.publishedAt}
                                    description={article.description}
                                    imgURL={article.urlToImage}
                                    url={article.url}
                                />
                            ))
                    }
                })}
            </div>
            <div className="secondGridMiddleArea">
                {}
            </div>

        </div>


    );
}
