import React, { useEffect, useState } from "react";

export default function Temp() {
   const [location, setLocation] = useState([]);
   const [icon, setIcon] = useState([]);
   const [threeDays, setThreeDays] = useState();

   useEffect(() => {
      getLocation();
   }, []);
   //ask for location
   function getLocation() {
      if (navigator.geolocation) {
         navigator.geolocation.getCurrentPosition(showPosition);
      } else {
         console.log("couldnt set the position");
      }
   }
   //POST lat and long
   //set geolocation
   function showPosition(position) {
      let lat = position.coords.latitude;
      let lon = position.coords.longitude;
      const locationData = { lat, lon };
      const options = {
         method: "POST",
         headers: {
            "Content-Type": "application/json",
         },
         body: JSON.stringify(locationData),
      };
      // fetch("/weather", options)
      //    .then((response) => response.json())
      //    .then((data) => {
      //       let weatherTemp = ((data.current.temp - 273.15) * 1.8 + 32).toFixed(
      //          0
      //       );
      //       let iconURL = data.current.weather[0].icon;
      //       setLocation(weatherTemp);
      //       setIcon(iconURL);
      //       setThreeDays(data);
      //       console.log(threeDays);
      //    });
   }

   return (
      <div>
         <div className="temperature">
            <img
               src={`http://openweathermap.org/img/wn/${icon}.png`}
               alt=""
               srcSet=""
            />
            {location} °F{" "}
         </div>
         <div className="blurBack"></div>
         <div className="popup">
            <div className="popup_inner">
               <div className="topInfo">
                  <div className="theDate">date</div>
                  <img
                     src={`http://openweathermap.org/img/wn/11n.png`}
                     alt=""
                     srcSet=""
                  />
                  <div className="theTemp">65 °F </div>
               </div>
               <div className="bottomInfo">
                  <div className="humidity">
                     <div>humidity:</div>
                     <div>visivility:</div>
                     <div>wind:</div>
                  </div>
                  <div className="timeofday">
                     <div>morning:</div>
                     <div>afternoon:</div>
                     <div>night:</div>
                  </div>
                  <div className="sunrise">
                     <div>
                        <div>sunrise</div>
                        <div>8:00am</div>
                     </div>
                     <div>
                        <div>sunset</div>
                        <div>8:00 pm</div>
                     </div>
                  </div>
               </div>
            </div>
            <div className="popup_inner">
               <div className="topInfo">
                  <div className="theDate">date</div>
                  <img
                     src={`http://openweathermap.org/img/wn/11n.png`}
                     alt=""
                     srcSet=""
                  />
                  <div className="theTemp">65 °F </div>
               </div>
               <div className="bottomInfo">
                  <div className="humidity">
                     <div>humidity:</div>
                     <div>visivility:</div>
                     <div>wind:</div>
                  </div>
                  <div className="timeofday">
                     <div>morning:</div>
                     <div>afternoon:</div>
                     <div>night:</div>
                  </div>
                  <div className="sunrise">
                     <div>
                        <div>sunrise</div>
                        <div>8:00am</div>
                     </div>
                     <div>
                        <div>sunset</div>
                        <div>8:00 pm</div>
                     </div>
                  </div>
               </div>
            </div>
            <div className="popup_inner">
               <div className="topInfo">
                  <div className="theDate">date</div>
                  <img
                     src={`http://openweathermap.org/img/wn/11n.png`}
                     alt=""
                     srcSet=""
                  />
                  <div className="theTemp">65 °F </div>
               </div>
               <div className="bottomInfo">
                  <div className="humidity">
                     <div>humidity:</div>
                     <div>visivility:</div>
                     <div>wind:</div>
                  </div>
                  <div className="timeofday">
                     <div>morning:</div>
                     <div>afternoon:</div>
                     <div>night:</div>
                  </div>
                  <div className="sunrise">
                     <div>
                        <div>sunrise</div>
                        <div>8:00am</div>
                     </div>
                     <div>
                        <div>sunset</div>
                        <div>8:00 pm</div>
                     </div>
                  </div>
               </div>
            </div>
         </div>
      </div>
   );
}
