import React ,{ useEffect, useState } from 'react';
import HeadlineWithImg from './HeadlineWithImg'


export default function TopSection() {
    const [items, setItems] = useState([]);
  
    useEffect( () => {
          loadData();
    }, []);
  
    const loadData = async () => {
         await fetch('/top')
        .then(res => res.json())
        .then(data => setItems(data.articles));
      }
      
    return (
        <div className="topSection">
            {items.filter(art => !!art.urlToImage).slice(14,18).map(article => (<HeadlineWithImg imgurl={article.urlToImage} headline={article.title} url={article.url} /> ))}
        </div>
    )
}
