         {items
            .filter((art) => !!art.urlToImage && !!art.title && !!art.url)
            .slice(0, 4)
            .map((article) => (
               <HeadlineWithImg
                  imgurl={article.urlToImage}
                  headline={article.title}
                  url={article.url}
               />
            ))}
      </div>
   );
}

