import React from 'react'

export default function SecondContainer() {
    const [items, setItems] = useState([]);
  
    useEffect( () => {
          loadData();
    }, []);
  
    const loadData = async () => {
         await fetch('/top')
        .then(res => res.json())
        .then(data => setItems(data.data.articles));
      }
      
    return (
        <div>
            <TopSection />
            <BottonSection />
        </div>
    )
}
