import Footer from "./components/Footer";
import React, { useState, createContext, useEffect } from "react";
import Languages from "./components/Languages";
import NavBarLinks from "./components/NavBarLinks";
import Temp from "./components/Temp";
import MainContainer from "./components/MainContainer";
import SecondContainer from "./components/SecondContainer";
import Date from "./components/Date";
import NavBarLinksTwo from "./components/NavBarLinksTwo";
import EmptySpace from "./components/EmptySpace";

import LinearDeterminate from "./subComponents/LinearDeterminate";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import TopicsPage from "./components/topicsPage";

import "./App.css";
import HomePage from "./components/HomePage";

export default function NavegationBar() {
   const [navData, setNavData] = useState();

   let firstEventToSend = (e, url) => {
      let valueToSend = {
         topic: e.target.innerText,
         url: url,
      };

      let options = {
         method: "POST",
         headers: {
            "Content-Type": "application/json",
         },
         body: JSON.stringify(valueToSend),
      };

      fetch(`${url}`, options)
         .then((response) => response.json())
         .then((data) => {
            setNavData(data.articles);
         });
   };

   return (
      <Router>
         <div>
            <div className="navbar">
               <ul className="navUl">
                  {/* <Temp /> */}
                  <h1 className="logo">
                     <Link to="/">
                        <p>~D|Fetch~</p>
                     </Link>
                  </h1>
                  <NavBarLinks
                     onClick={(e) => {
                        firstEventToSend(e, "/topics");
                     }}
                     className="linksNav"
                  />
                  <NavBarLinksTwo
                     onClick={(e) => {
                        firstEventToSend(e, "/sources");
                     }}
                     className="linksNav"
                  />
                  <Languages
                     onClick={(e) => {
                        firstEventToSend(e, "/lang");
                     }}
                     className="linksNav"
                  />
               </ul>
               <Date />
            </div>

            <EmptySpace />

            <Switch>
               <Route exact path="/search">
                  {navData ? (
                     <TopicsPage topicsData={navData} />
                  ) : (
                     <LinearDeterminate />
                  )}
               </Route>
               <Route exact path="/topics">
                  {navData ? (
                     <TopicsPage topicsData={navData} />
                  ) : (
                     <LinearDeterminate />
                  )}
               </Route>
               <Route exact path="/sources">
                  <TopicsPage />
               </Route>
               <Route exact path="/">
                  <HomePage />
               </Route>
            </Switch>
            <Footer />
         </div>
      </Router>
   );
}
