import React, { useEffect, useState } from "react";
import XlArticle from "./XlArticle";
import HeadlineWithImg from "./HeadlineWithImg";

export default function SecondGridFirstArea() {
   const [items, setItems] = useState([]);

   useEffect(() => {
      loadData();
   }, []);

   const loadData = async () => {
      await fetch("/tech")
         .then((res) => res.json())
         .then((data) => setItems(data.articles));
   };

   return (
      <div className="left-section-news">
         {items
            .filter((art) => !!art.urlToImage && !!art.url)
            .slice(0, 1)
            .map((article) => (
               <XlArticle
                  imgURL={article.urlToImage}
                  headline={article.title}
                  link={article.url}
               />
            ))}
         <div className="headlineSideBySide">
            {items
               .filter((art, index) => !!art.urlToImage && !!art.url && items.indexOf(art) === index)
               .slice(1, 3)
               .map((article) => (
                  <HeadlineWithImg
                     imgurl={article.urlToImage}
                     headline={article.title}
                     url={article.url}
                  />
               ))}
            {/* {items
               .filter((art, index) => !!art.urlToImage && !!art.url && items.indexOf(art) === index)
               .slice(2, 3)
               .map((article) => (
                  <HeadlineWithImg
                     imgurl={article.urlToImage}
                     headline={article.title}
                     url={article.url}
                  />
               ))} */}
         </div>
         <div className="headlineSideBySide">
            {items
               .filter((art, index) => !!art.urlToImage && !!art.url && items.indexOf(art) === index)
               .slice(3, 4)
               .map((article) => (
                  <HeadlineWithImg
                     imgurl={article.urlToImage}
                     headline={article.title}
                     url={article.url}
                  />
               ))}
            {items
               .filter((art, index) => !!art.urlToImage && !!art.url && items.indexOf(art) === index)
               .slice(4, 5)
               .map((article) => (
                  <HeadlineWithImg
                     imgurl={article.urlToImage}
                     headline={article.title}
                     url={article.url}
                  />
               ))}
         </div>
      </div>
   );
}
