import React from "react";
import BigArticle from "../subComponents/BigArticle";
import LinearDeterminate from "../subComponents/LinearDeterminate";
import useFetch from "../subComponents/myFetchHook";
import HeadlineWithImg from "../subComponents/HeadlineWithImg";
import XlArticle from "../subComponents/XlArticle";

function Tablet() {
   const { data, isLoading } = useFetch("/home/top");
   return (
      <div className="tablet_show">
         <div className="secondGrid">
            <div className="left-section-news">
               {data
                  .filter((art) => !!art.urlToImage && !!art.url)
                  .slice(0, 1)
                  .map((article) => (
                     <XlArticle
                        imgURL={article.urlToImage}
                        headline={article.title}
                        link={article.url}
                     />
                  ))}
               <div className="headlineSideBySide">
                  {data
                     .filter(
                        (art, index) =>
                           !!art.urlToImage &&
                           !!art.url &&
                           data.indexOf(art) === index
                     )
                     .slice(1, 2)
                     .map((article) => (
                        <HeadlineWithImg
                           imgurl={article.urlToImage}
                           headline={article.title}
                           url={article.url}
                        />
                     ))}
                  {data
                     .filter(
                        (art, index) =>
                           !!art.urlToImage &&
                           !!art.url &&
                           data.indexOf(art) === index
                     )
                     .slice(3, 4)
                     .map((article) => (
                        <HeadlineWithImg
                           imgurl={article.urlToImage}
                           headline={article.title}
                           url={article.url}
                        />
                     ))}
               </div>
               <div className="headlineSideBySide">
                  {data
                     .filter(
                        (art, index) =>
                           !!art.urlToImage &&
                           !!art.url &&
                           data.indexOf(art) === index
                     )
                     .slice(5, 6)
                     .map((article) => (
                        <HeadlineWithImg
                           imgurl={article.urlToImage}
                           headline={article.title}
                           url={article.url}
                        />
                     ))}
                  {data
                     .filter(
                        (art, index) =>
                           !!art.urlToImage &&
                           !!art.url &&
                           data.indexOf(art) === index
                     )
                     .slice(7, 8)
                     .map((article) => (
                        <HeadlineWithImg
                           imgurl={article.urlToImage}
                           headline={article.title}
                           url={article.url}
                        />
                     ))}
               </div>
               <div className="headlineSideBySide">
                  {data
                     .filter(
                        (art, index) =>
                           !!art.urlToImage &&
                           !!art.url &&
                           data.indexOf(art) === index
                     )
                     .slice(9, 10)
                     .map((article) => (
                        <HeadlineWithImg
                           imgurl={article.urlToImage}
                           headline={article.title}
                           url={article.url}
                        />
                     ))}
                  {data
                     .filter(
                        (art, index) =>
                           !!art.urlToImage &&
                           !!art.url &&
                           data.indexOf(art) === index
                     )
                     .slice(11, 12)
                     .map((article) => (
                        <HeadlineWithImg
                           imgurl={article.urlToImage}
                           headline={article.title}
                           url={article.url}
                        />
                     ))}
               </div>
            </div>
            ) : (
            <LinearDeterminate />
            );
            <div className="secondGridMiddleArea">
               {data ? (
                  data
                     .filter(
                        (art) =>
                           !!art.description && !!art.urlToImage && !!art.author
                     )
                     .slice(0, 5)
                     .map((article) => (
                        <BigArticle
                           key={article.url}
                           headline={article.title}
                           author={article.author}
                           time={article.publishedAt}
                           description={article.description}
                           imgURL={article.urlToImage}
                           url={article.url}
                        />
                     ))
               ) : (
                  <LinearDeterminate />
               )}
            </div>
         </div>
      </div>
   );
}

export default Tablet;
