import React, { useState, useEffect } from "react";
import SmallHeadlines from "./SmallHeadlines";
import HeadlineWithImg from "./HeadlineWithImg";
import useFetch from './myFetchHook'

function FirstSectionNews() {


   const { data, isLoading, hasError, errorMessage, updateUrl } = useFetch("/top")

   return (
      <div className="firstSectionNews">
         <div className="blackTitle">Top Stories</div>
         {data.slice(4, 14).map((article) => (
            <SmallHeadlines headline={article.title} url={article.url} />
         ))}
      </div>
   );
}

export default FirstSectionNews;
