import React, { useEffect, useState } from 'react'

export default useFetch = (url) => {
    [items, setItems] = useState()


    useEffect(() => {
        fetch(url)
            .then((res) => res.json())
            .then((data) => setItems(data.data.articles));
    })
}

useFetch()
