import React, {useState , useEffect} from 'react';
import SmallHeadlines from './SmallHeadlines';

function RigthSection() {

    const [items, setItems] = useState([]);
  
    useEffect( () => {
          loadData();
    }, []);
  
    const loadData = async () => {
         await fetch('/bussiness')
        .then(res => res.json())
        .then(data => setItems(data.data.articles));
      }
      
     

return( 
    <div className="right-section-news">
        {items.slice(0,9).map(article => (<SmallHeadlines headline={article.title}/> ))}
       
    </div>
);
}


export default RigthSection;
