import { useState, useEffect } from 'react'
import useFetch from './myFetchHook'

const useEvent = ( e ) => {
       
      let valueToSend = {
         topic: e.target.innerText
      }
      const options = {
         method: 'POST',
         headers: {
            'Content-Type': 'application/json'
         },
         body: JSON.stringify(valueToSend)
      };
      const {data} = useFetch("/topics")
         return {data}
   
}
export default useEvent;
