import React, { useState } from "react";
import BigArticle from "./BigArticle";

function TopicsPage(props) {
   const [articles, setArticles] = useState();
   const [state, setState] = useState("/top");
   const topicsData = props.topicsData;
   const url = topicsData.url;
   setState(url);

   const valueToSend = topicsData.topic;
   console.log(topicsData);
   console.log();

   let options = {
      method: "POST",
      headers: {
         "Content-Type": "application/json",
      },
      body: JSON.stringify(valueToSend),
   };
   if (url) {
      fetch(`${url}`, options)
         .then((response) => response.json())
         .then((data) => {
            setArticles(data.articles);
         });
   } else {
      console.log("error");
   }

   return (
      <div className="navbar-container">
         <div className="left-section-news">
            {articles ? (
               articles
                  .filter(
                     (art) =>
                        !!art.description && !!art.urlToImage && !!art.author
                  )
                  .slice(0, 15)
                  .map((article) => (
                     <BigArticle
                        key={article.url}
                        headline={article.title}
                        author={article.author}
                        time={article.publishedAt}
                        description={article.description}
                        imgURL={article.urlToImage}
                        url={article.url}
                     />
                  ))
            ) : (
               <div>nothing right now</div>
            )}
         </div>
      </div>
   );
}

export default TopicsPage;
