import React , { useState , useEffect } from 'react';
import BigArticle from './BigArticle';
import XlArticle from './XlArticle'

function LeftSideNews() {
    const [items, setItems] = useState([]);
    
  
    useEffect( () => {
          loadData();
    }, []);
  
    const loadData = async () => {
         await fetch('/trump')
        .then(res => res.json())
        .then(data => setItems(data.articles));
      }
      
      

    return (
       <div className="left-section-news">
         {items.slice(0,1).map(article => (<XlArticle imgURL={article.urlToImage} headline={article.title} url={article.url} />))}
         {items.filter(art => !art.description&&!art.urlToImage).slice(1,3).map(article => ( <BigArticle key={article.url} 
                                                    headline ={article.title} 
                                                    author={article.author}
                                                    time={article.publishedAt}
                                                    description={article.content} 
                                                    imgURL={article.urlToImage} 
                                                    url={article.url}/>))}
        
       </div>
    );
}

export default LeftSideNews;
