import React, { useEffect, useState } from "react";

export default function Temp() {
   const [location, setLocation] = useState([]);
   const [icon, setIcon] = useState([]);

   useEffect(() => {
      getLocation();
   }, []);
   //ask for location
   function getLocation() {
      if (navigator.geolocation) {
         navigator.geolocation.getCurrentPosition(showPosition);
      } else {
         console.log("couldnt set the position");
      }
   }
   //POST lat and long
   //set geolocation
   function showPosition(position) {
      let lat = position.coords.latitude;
      let lon = position.coords.longitude;
      const key = "3ba77f7647194c378df175214200610";
      let api = `http://api.weatherapi.com/v1/current.json?key=${key}&q=${lat},${lon}`;
      fetch(api)
         .then((response) => response.json())
         .then((data) => {
            let weatherTemp = data.current.temp_f
            let iconURL = data.current.condition.icon
            setLocation(weatherTemp.toFixed)
            setIcon(iconURL)
            console.log(data);
         })
         .catch((err) => {
            console.log("error sending weather data");
         });
   }

