import React, { useState, createContext } from "react";
import Languages from "./Languages";
import NavBarLinks from "./NavBarLinks";
import Temp from "./Temp";
import MainContainer from "./MainContainer";
import SecondContainer from "./SecondContainer";
import Date from "./Date";
import NavBarLinksTwo from "./NavBarLinksTwo";
import EmptySpace from "./EmptySpace";
import BigArticle from "./BigArticle";
import LinearDeterminate from "./LinearDeterminate";
import useEvent from "./useEventHook";
import useFetch from "./myFetchHook";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

export const DataProvider = createContext();

export default function NavegationBar() {
   const [navData, setNavData] = useState();
   const [sourceData, setSourceData] = useState();
   const [langData, setLangData] = useState();

   let firstEventToSend = (e) => {
      let valueToSend = {
         topic: e.target.innerText,
      };
      const options = {
         method: "POST",
         headers: {
            "Content-Type": "application/json",
         },
         body: JSON.stringify(valueToSend),
      };
      fetch("/topics", options)
         .then((response) => response.json())
         .then((data) => {
            setNavData(data.articles);
            console.log(data.articles);
         });
   };

   let secondEventToSend = (e) => {
      let valueToSend = {
         topic: e.target.innerText,
      };
      const options = {
         method: "POST",
         headers: {
            "Content-Type": "application/json",
         },
         body: JSON.stringify(valueToSend),
      };
      fetch("/sources", options)
         .then((response) => response.json())
         .then((data) => {
            setSourceData(data.articles);
            console.log(data.articles);
         });
   };
   let thirdEventToSend = (e) => {
      let valueToSend = {
         topic: e.target.innerText,
      };
      console.log(e.target.innerText);

      const options = {
         method: "POST",
         headers: {
            "Content-Type": "application/json",
         },
         body: JSON.stringify(valueToSend),
      };
      fetch("/lang", options)
         .then((response) => response.json())
         .then((data) => {
            setLangData(data.articles);
            console.log(data);
         });
   };

   return (
      <Router>
         <div>
            <div className="navbar">
               <ul className="navUl">
                  {/* <Temp /> */}
                  <h1 className="logo">
                     <Link to="/">
                        <p>~D|Fetch~</p>
                     </Link>
                  </h1>
                  <NavBarLinks
                     onClick={firstEventToSend}
                     className="linksNav"
                  />
                  <NavBarLinksTwo
                     onClick={secondEventToSend}
                     className="linksNav"
                  />
                  <Languages onClick={thirdEventToSend} className="linksNav" />
               </ul>
               <Date />
            </div>

            <EmptySpace />

            <Switch>
               <Route exact path="/topics">
                  {navData ? (
                     <div className="navbar-container">
                        <div className="left-section-news">
                           {navData
                              .filter(
                                 (art) =>
                                    !!art.description &&
                                    !!art.urlToImage &&
                                    !!art.author
                              )
                              .slice(0, 10)
                              .map(({ article }) => (
                                 <BigArticle
                                    key={url}
                                    headline={title}
                                    author={author}
                                    time={publishedAt}
                                    description={description}
                                    imgURL={urlToImage}
                                    url={url}
                                 />
                              ))}
                        </div>
                     </div>
                  ) : (
                     <LinearDeterminate />
                  )}
               </Route>
               <Route exact path="/sources">
                  {sourceData ? (
                     <div className="navbar-container">
                        <div className="left-section-news">
                           {sourceData
                              .filter(
                                 (art) =>
                                    !!art.description &&
                                    !!art.urlToImage &&
                                    !!art.author
                              )
                              .slice(0, 10)
                              .map((article) => (
                                 <BigArticle
                                    key={article.url}
                                    headline={article.title}
                                    author={article.author}
                                    time={article.publishedAt}
                                    description={article.description}
                                    imgURL={article.urlToImage}
                                    url={article.url}
                                 />
                              ))}
                        </div>
                     </div>
                  ) : (
                     <LinearDeterminate />
                  )}
               </Route>
               <Route exact path="/lang">
                  {langData ? (
                     <div className="navbar-container">
                        <div className="left-section-news">
                           {langData
                              .filter(
                                 (art) =>
                                    !!art.description &&
                                    !!art.urlToImage &&
                                    !!art.author
                              )
                              .slice(0, 10)
                              .map((article) => (
                                 <BigArticle
                                    key={article.url}
                                    headline={article.title}
                                    author={article.author}
                                    time={article.publishedAt}
                                    description={article.description}
                                    imgURL={article.urlToImage}
                                    url={article.url}
                                 />
                              ))}
                        </div>
                     </div>
                  ) : (
                     <LinearDeterminate />
                  )}
               </Route>
               <Route exact path="/">
                  {/* <MainContainer />
                  <SecondContainer /> */}
               </Route>
            </Switch>
         </div>
      </Router>
   );
}
