import React, {useState , useEffect} from 'react';
import SmallHeadlines from './SmallHeadlines';

function RigthSection() {

    const [items, setItems] = useState([]);
  
    useEffect( () => {
          loadData();
    }, [items]);
  
    const loadData = async () => {
         await fetch('/bussiness')
        .then(res => res.json())
        .then(data => setItems(data.data.articles));
      }
      
     

return( 
    <div className="right-section-news">
        
        {items.slice(10,11).map(article => (<HeadlineWithImg imgurl={article.urlToImage} headline={article.title} url={article.url} /> ))}
        {items.slice(11,17).map(article => (<SmallHeadlines headline={article.title} url={article.url}/>))}
    </div>
);
}


export default RigthSection;
