import React from "react";
import BigArticle from "./BigArticle";
import CircularIndeterminate from "./LinearDeterminate";
import useFetch from "./myFetchHook";

export default function SecondGridMiddleArea() {
   const { data, isLoading } = useFetch("/sports");

   if (isLoading) {
      return <CircularIndeterminate />;
   } else {
      return (
         <div className="secondGridMiddleArea">
            {data
               .filter(
                  (art) => !!art.description && !!art.urlToImage && !!art.author
               )
               .slice(0, 5)
               .map((article) => (
                  <BigArticle
                     key={article.url}
                     headline={article.title}
                     author={article.author}
                     time={article.publishedAt}
                     description={article.description}
                     imgURL={article.urlToImage}
                     url={article.url}
                  />
               ))}
         </div>
      );
   }
}
