import React, {useEffect, useState} from 'react'
import BigArticle from './BigArticle';
import XlArticle from './XlArticle'


export default function SecondGridFirstArea() {
    const [items, setItems] = useState([]);
  
    useEffect( () => {
          loadData();
    }, []);
  
    const loadData = async () => {
         await fetch('/top')
        .then(res => res.json())
        .then(data => setItems(data.data.articles));
      }
      
    

return( 
    <div>
        {items.filter(art => !!art.urlToImage&&!!art.url).slice(0,1).map(article => (<XlArticle imgURL={article.urlToImage} headline={article.title} url={article.url} />))}
        {items.filter(art => !!art.urlToImage&&!!art.url).slice(1,2).map(article => (<XlArticle imgURL={article.urlToImage} headline={article.title} url={article.url} />))}
        
    </div>
    )
}
