import Footer from "./components/Footer";
import React, { useState, createContext, useEffect } from "react";
import NavBarLinksLang from "./components/NavBarLinksLang";
import NavBarLinksTopics from "./components/NavBarLinksTopics";
import Temp from "./components/Temp";
import MainContainer from "./components/MainContainer";
import SecondContainer from "./components/SecondContainer";
import Date from "./components/Date";
import NavBarLinksSources from "./components/NavBarLinksSources";
import EmptySpace from "./components/EmptySpace";
import SearchPage from "./components/SearchPage";
import SourcesPage from "./components/SourcesPage";
import LinearDeterminate from "./subComponents/LinearDeterminate";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import TopicsPage from "./components/TopicsPage";

import "./App.css";
import HomePage from "./components/HomePage";

export default function NavegationBar() {
   const [navData, setNavData] = useState();

   let firstEventToSend = (e, url) => {
      let valueToSend = {
         topic: e.target.innerText,
         url: url,
      };

      let options = {
         method: "POST",
         headers: {
            "Content-Type": "application/json",
         },
         body: JSON.stringify(valueToSend),
      };

      fetch(`${url}`, options)
         .then((response) => response.json())
         .then((data) => {
            setNavData(data.articles);
         });
   };

   return (
      <Router>
         <div>
            <div className="navbar">
               <ul className="navUl">
                  {/* <Temp /> */}
                  <h1 className="logo">
                     <Link to="/">
                        <p>~D|Fetch~</p>
                     </Link>
                  </h1>
                  <NavBarLinksTopics />
                  <NavBarLinksSources />
                  <NavBarLinksLang />
               </ul>
               <Date />
            </div>

            <EmptySpace />

            <Switch>
               <Route exact path="/search">
                  <SearchPage />
               </Route>

               <Route exact path="/topics" component={Topics}>
                  <div>jajajajakakakakaka</div>
               </Route>

               <Route exact path="/sources">
                  <SourcesPage />
               </Route>

               <Route exact path="/">
                  <HomePage />
               </Route>
            </Switch>
            <Footer />
         </div>
      </Router>
   );
}
