import React, { useState } from "react";
import ComPage from "../subRoutes/comPage";

function SearchPage() {
   const [state, setstate] = useState();

   const axios = require("axios");
   const qs = require("qs");
   let data = qs.stringify({
      question: "miami dade",
      language: "en",
   });
   let config = {
      method: "post",
      url: "/search",
      headers: {
         "Content-Type": "application/x-www-form-urlencoded",
      },
      data: data,
   };

   axios(config)
      .then((response) => {
         setstate(JSON.stringify(response.data));
      })
      .catch((error) => {
         console.log(error);
      });

   return <ComPage news={state} />;
}

export default SearchPage;
