import { useEffect, useState } from "react";


export default function TopSection() {
    const [items, setItems] = useState([]);
  
    useEffect( () => {
          loadData();
    }, []);
  
    const loadData = async () => {
         await fetch('/top')
        .then(res => res.json())
        .then(data => setItems(data.data.articles));
      }
      
    return (
        <div>
            {items.filter(art => !!art.urlToImage&&!!art.title&&!!art.url).slice(15,19).map(article => (<HeadlineWithImg imgurl={article.urlToImage} headline={article.title} url={article.url} /> ))}
        </div>
    )
}
