import React , { useState , useEffect } from 'react';
import BigArticle from './BigArticle';

function LeftSideNews() {
    const [news, setNews] = useState()

    useEffect(() => {
      fetch('/source')
            .then(res => {
                return res.json();
            })
            .then(news => {
                setNews(news);
                console.log(news)
            })
            .catch(err => {
                console.log('there is an error')
            });
       
    }, [])


    return( <div className="left-section-news">
                <BigArticle headline= "lorem10 lorem 10" />
                <BigArticle />
                <BigArticle />
                <BigArticle />
                
            </div>);
}

