import React, { useEffect, useState } from 'react'

export default useFetch = (url) => {
    [items, setItems] = useState(null)


    useEffect(() => {
        const fetchData = async () => {
            fetch(url)
                .then((response) => response.json())
                .then((result) => {
                    setItems(result)
                })
        }
        fetchData()
    }, [])
    return { items }
}
