function MiddleSection() {

    const [items, setItems] = useState([]);
  
    useEffect( () => {
          loadData();
    }, [items]);
  
    const loadData = async () => {
         await fetch('/source')
        .then(res => res.json())
        .then(data => setItems(data.data.articles));
      }
      
    

return( 
    <div className="middle-section-news">
        {items.slice(4,12).map(article => (<SmallHeadlines headline={article.title} url={article.url} />))}
       
    </div>
);
}


export default MiddleSection;
