import React from "react";
import Languages from "./Languages";
import NavBarLinks from "./NavBarLinks";
import Temp from "./Temp";
import Date from "./Date"
import NavBarLinksTwo from "./NavBarLinksTwo";

export default function NavBar() {
   let eventToSend = (e) => {
      let valueToSend = {
         topic: e.target.text
      }
      const options = {
         method: 'POST',
         headers: {
            'Content-Type': 'application/json'
         },
         body: JSON.stringify(valueToSend)
      };
      fetch('/topics', options)
         .then((response) => response.json())
         .then((data) => {
            console.log(data);
         })
   }
   return (
      <div className="navbar" onClick={eventToSend}>
         <ul className="navUl">
            {/* <Temp /> */}
            <h1 className="logo">
               <a href="index.js">~D|Fetch~</a>
            </h1>
            <NavBarLinks
               className="linksNav"
               name="Topics"
               link1="Health"
               link2="Science"
               link3="Sports"
               link4="Entertaiment"
               link5="Business"
               link6="Technology"
            />
            <NavBarLinksTwo
               className="linksNav"
               name="Sources"
               link1="The Wall Street Journal"
               link2="National Geograpic"
               link3="Associated Press"
               link4="Time"
               link5="Reuters"
               link6="BBC"
               link7="CNN"
               link8="ESPN"
            />
            <Languages
               className="linksNav"
               name="Lan"
               link1="English"
               link2="Spanish"
            />
         </ul>

         <Date />
      </div>
   );
}
