import React, { useState, createContext } from "react";
import Languages from "./Languages";
import NavBarLinks from "./NavBarLinks";
import Temp from "./Temp";
import TopicsPage from "./TopicsPage"
import Date from "./Date"
import NavBarLinksTwo from "./NavBarLinksTwo";
import MainContainer from "./MainContainer"
import SecondContainer from "./SecondContainer"
import EmptySpace from "./EmptySpace";
import BigArticle from "./BigArticle";
import {
   BrowserRouter as Router,
   Switch,
   Route,
   Link
} from "react-router-dom";


export const DataProvider = createContext()

export default function NavegationBar() {
   const [navData, setNavData] = useState();

   let eventToSend = (e) => {
      let valueToSend = {
         topic: e.target.text
      }
      const options = {
         method: 'POST',
         headers: {
            'Content-Type': 'application/json'
         },
         body: JSON.stringify(valueToSend)
      };
      fetch('/topics', options)
         .then((response) => response.json())
         .then((data) => {
            setNavData(data);
         })
   }



   return (
      <DataProvider.Provider value={[navData, setNavData]}>

         <Router>
            <div>
               <div className="navbar" >
                  <ul className="navUl">
                     {/* <Temp /> */}
                     <h1 className="logo">
                        <a href="/">~D|Fetch~</a>

                     </h1>

                     <NavBarLinks onClick={eventToSend}
                        className="linksNav"
                        name="Topics"
                        link1="Health"
                        link2="Science"
                        link3="Sports"
                        link4="Entertaiment"
                        link5="Business"
                        link6="Technology"
                     />
                     <NavBarLinksTwo
                        className="linksNav"
                        name="Sources"
                        link1="The Wall Street Journal"
                        link2="National Geograpic"
                        link3="Associated Press"
                        link4="Time"
                        link5="Reuters"
                        link6="BBC"
                        link7="CNN"
                        link8="ESPN"
                     />
                     <Languages
                        className="linksNav"
                        name="Lan"
                        link1="English"
                        link2="Spanish"
                     />
                  </ul>

                  <Date />

               </div>
               <EmptySpace />
               <Switch>

                  <Route exact path="/topics">
                     <div className="secondContainer">hello
            <div className="left-section-news">
                           {navData
                              .filter(
                                 (art) => !!art.description && !!art.urlToImage && !!art.author
                              )
                              .slice(0, 3)
                              .map((article) => (
                                 <BigArticle
                                    key={article.url}
                                    headline={article.title}
                                    author={article.author}
                                    time={article.publishedAt}
                                    description={article.description}
                                    imgURL={article.urlToImage}
                                    url={article.url}
                                 />
                              ))}
                        </div>

                        <div className="secondGridMiddleArea">
                           {navData
                              .filter(
                                 (art) => !!art.description && !!art.urlToImage && !!art.author
                              )
                              .slice(4, 7)
                              .map((article) => (
                                 <BigArticle
                                    key={article.url}
                                    headline={article.title}
                                    author={article.author}
                                    time={article.publishedAt}
                                    description={article.description}
                                    imgURL={article.urlToImage}
                                    url={article.url}
                                 />
                              ))}
                        </div>

                     </div>
                  </Route>
                  <Route exact path="/">
                     {/* <MainContainer />
                     <SecondContainer /> */}
                  </Route>
               </Switch>
            </div>
         </Router>
      </DataProvider.Provider>
   );
}
