import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";

import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";

import Typography from "@material-ui/core/Typography";

import useFetch from "../subComponents/myFetchHook";
import LinearDeterminate from "../subComponents/LinearDeterminate";

const useStyles = makeStyles({
   root: {
      borderRadius: 20,
      maxWidth: 305,
      margin: 20,
   },
   media: {
      height: 190,
      maxHeight: 190,
      objectFit: "cover",
   },
});

function PhoneScreens() {
   const { data, isLoading } = useFetch("/topics/business");
   console.log(data);

   const classes = useStyles();

   return (
      <div className="phone_show">
         {data ? (
            data
               .filter((art) => !!art.urlToImage && !!art.url)
               .slice(0, 12)
               .map((article) => (
                  <Card className={classes.root}>
                     <CardActionArea href={article.url} target="_blank">
                        <CardMedia
                           className={classes.media}
                           image={article.urlToImage}
                        />
                        <CardContent>
                           <Typography gutterBottom variant="h6" component="h2">
                              {article.title}
                           </Typography>
                        </CardContent>
                     </CardActionArea>
                  </Card>
               ))
         ) : (
            <LinearDeterminate />
         )}
      </div>
   );
}

export default PhoneScreens;
