import React, { useEffect, useState } from "react";

export default function Temp() {
   const [location, setLocation] = useState([]);
   const [icon, setIcon] = useState([]);

   useEffect(() => {
      getLocation();
   }, []);
   //ask for location
   function getLocation() {
      if (navigator.geolocation) {
         navigator.geolocation.getCurrentPosition(showPosition);
      } else {
         console.log("couldnt set the position");
      }
   }
   //POST lat and long
   //set geolocation
   function showPosition(position) {
      let lat = position.coords.latitude;
      let lon = position.coords.longitude;
      const locationData = { lat, lon };
      const options = {
         method: 'POST',
         headers: {
            'Content-Type': 'application/json'
         },
         body: JSON.stringify(locationData)
      };
      fetch('/weather', options)

   }
   async function fixFetch() {
      const response = await fetch('/weather')
      console.log(response)
   }
   fetch('/weather')
      .then((response) => response.json())
      .then((data) => {
         console.log(data)
         let weatherTemp = (data.current.temp_f).toFixed(0)
         let iconURL = data.current.condition.icon
         setLocation(weatherTemp)
         setIcon(iconURL)
         console.log(data);
      })
      .catch((err) => {
         console.log("error getting weather data from server");
      });
