import React, { useState, useEffect } from "react";
import ComPage from "../subRoutes/comPage";

function SearchPage({ liftedState }) {
   const [state, setstate] = useState();

   let myHeaders = new Headers();
   myHeaders.append("Content-Type", "application/x-www-form-urlencoded");

   let urlencoded = new URLSearchParams();
   urlencoded.append("question", `${liftedState.input}`);
   urlencoded.append("language", "en");

   let requestOptions = {
      method: "POST",
      headers: myHeaders,
      body: urlencoded,
      redirect: "follow",
   };

   useEffect(() => {
      if (!liftedState.toggle) {
         fetch("/search", requestOptions)
            .then((response) => response.text())
            .then((result) => setstate(result))
            .catch((error) => console.log("error", error));
      }
   }, []);

   return (
      <div className="emptySpace">
         <div>
            <div>state: {state}</div>
            input:{liftedState.input} <br />
            toggle:{liftedState.toggle}
         </div>
         {/* {liftedState.toggle ? (
            // <ComPage news={state} />
            <div>{state}</div>
         ) : (
            <div>PLEASE START SEARCHING OR RETURN HOME</div>
         )} */}
      </div>
   );
}

export default SearchPage;
