import React, { useState, useEffect } from 'react';
import Moment from 'react-moment';
import 'moment-timezone';

function Date() {
    const[date, setDate] = useState("");

    useEffect( () => {
        setDate(moment().format("Do dddd MMMM gggg"))
    }, []);



return(<p id="date">{date}</p>)
}

export default Date;
