import React , { useState , useEffect } from 'react';
import BigArticle from './BigArticle';

function LeftSideNews() {
    const [news, setNews] = useState({})

    useEffect(() => {
      fetch('/source')
            .then(res => {
                if(res.ok) {
                    return res.json();
                }else{
                    throw Error("error getting the data from server");
                }
            })
            .then(news => {
                setNews(news);
                console.log(news)
            })
            .catch(err => {
                console.log('there is an error')
            });
       
    }, [])


    return( <div className="left-section-news">
                <BigArticle headline={news.article[0].title} />
                <BigArticle />
                <BigArticle />
                <BigArticle />
                
            </div>);
}

//headline:"" description:"" imgURL:"" 

export default LeftSideNews;
