import React , { useState , useEffect } from 'react';
import BigArticle from './BigArticle';

function LeftSideNews() {
    const [news, setNews] = useState([])

    useEffect(() => {
      fetch('/source')
            .then(res => {
                return res.json();
            })
            .then(news => {
                setNews({news});
                let newOne = news.data.articles[0]
                console.log(newOne) 
            })
            .catch(err => {
                console.log('there is an error')
            });
       
    }, [])

    
    return( <div className="left-section-news">
               
            <BigArticle headline={ news.data.articles[0].title} description="hello" imgURL=""/>
                
              
            </div>);
}



//headline="" description="" imgURL="" headline={news[0].title}

export default LeftSideNews;
