import React, { useState, useEffect } from "react";
import SmallHeadlines from "./SmallHeadlines";
import HeadlineWithImg from "./HeadlineWithImg";

function RigthSection() {
   const [items, setItems] = useState([]);

   useEffect(() => {
      loadData();
   }, []);

   const loadData = async () => {
      await fetch("/top")
         .then((res) => res.json())
         .then((data) => setItems(data.data.articles));
   };

   return (
      <div className="right-section-news">
         {items
            .filter((art) => !!art.urlToImage && !!art.title && !!art.url)
            .slice(0, 4)
            .map((article) => (
               <HeadlineWithImg
                  imgurl={article.urlToImage}
                  headline={article.title}
                  url={article.url}
               />
            ))}
      </div>
   );
}

export default RigthSection;
