import React from "react";
import HeadlineWithImg from "./HeadlineWithImg";
import CircularIndeterminate from "./LinearDeterminate";
import useFetch from "./myFetchHook";

export default function TopSection() {
   const url = "/top";
   const { data, isLoading, hasError, errorMessage, updateUrl } = useFetch(url);

   if (isLoading) {
      return <CircularIndeterminate />;
   } else {
      return (
         <div className="topSection">
            {data
               .filter((art) => !!art.urlToImage)
               .slice(14, 19)
               .map((article) => (
                  <div className="topSectionDiv">
                     <HeadlineWithImg
                        imgurl={article.urlToImage}
                        headline={article.title}
                        url={article.url}
                     />
                  </div>
               ))}
         </div>
      );
   }
}
