import React, {useEffect, useState} from 'react'


export default function SecondGridFirstArea() {
    const [items, setItems] = useState([]);
  
    useEffect( () => {
          loadData();
    }, []);
  
    const loadData = async () => {
         await fetch('/top')
        .then(res => res.json())
        .then(data => setItems(data.data.articles));
      }
      
    

return( 
    <div className="firstSectionNews">
        <div className="blackTitle">Top Stories</div>
        {items.slice(3,15).map(article => (<SmallHeadlines headline={article.title} url={article.url} />))}
       
    </div>
    )
}
