import React, {useState , useEffect} from 'react';
import SmallHeadlines from './SmallHeadlines';
import HeadlineWithImg from './HeadlineWithImg';

function FirstSectionNews() {

    const [items, setItems] = useState([]);
  
    useEffect( () => {
          loadData();
    }, []);
  
    const loadData = async () => {
         await fetch('/tech')
        .then(res => res.json())
        .then(data => setItems(data.data.articles));
      }
      
    

return( 
    <div className="firstSectionNews">
        {items.slice(4,7).map(article => (<HeadlineWithImg imgurl={article.urlToImage} headline={article.title} url={article.url} />))}
       
    </div>
);
}


export default FirstSectionNews;
